function CRM() {
  return (
    <div>
      <h2>מערכת CRM</h2>
      <p>כאן תוצג רשימת הלקוחות, היסטוריית השיחות, מוצרים וקופונים.</p>
      {/* ניתן להוסיף טבלאות, חיפושים וממשק ניהול לקוחות */}
    </div>
  );
}

export default CRM;
