import React, { useState, useRef } from 'react';

const EnhancedTranscript = ({ 
  isRecording,
  onTranscriptUpdate,
  onSummaryUpdate 
}) => {
  const [transcript, setTranscript] = useState([]);
  const [summary, setSummary] = useState('');
  const [tasks, setTasks] = useState([]);
  const scrollRef = useRef(null);

  // Simulated transcript entry structure
  const addTranscriptEntry = (speaker, text) => {
    const newEntry = {
      id: Date.now(),
      speaker,
      text,
      timestamp: new Date().toLocaleTimeString(),
    };
    setTranscript(prev => [...prev, newEntry]);

    // Auto scroll to bottom
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 h-full">
      {/* Main Transcript Panel */}
      <div className="lg:col-span-2 space-y-4">
        <div 
          ref={scrollRef}
          className="bg-white rounded-lg border border-stone-200 p-4 h-[400px] overflow-y-auto"
        >
          {transcript.map(entry => (
            <div 
              key={entry.id} 
              className={`mb-4 p-3 rounded-lg ${
                entry.speaker === 'יועץ' 
                  ? 'bg-finance-50 mr-8' 
                  : 'bg-trust-50 ml-8'
              }`}
            >
              <div className="flex justify-between text-sm text-gray-500 mb-1">
                <span className="font-medium">{entry.speaker}</span>
                <span>{entry.timestamp}</span>
              </div>
              <p className="text-finance-300">{entry.text}</p>
            </div>
          ))}
        </div>

        {/* Quick Actions */}
        <div className="flex gap-2">
          <button 
            className="px-4 py-2 bg-finance-300 text-white rounded-lg hover:bg-finance-400"
            onClick={() => {/* Export to CRM */}}
          >
            שמור ב-CRM
          </button>
          <button 
            className="px-4 py-2 bg-trust-400 text-white rounded-lg hover:bg-trust-500"
            onClick={() => {/* Generate email */}}
          >
            צור מייל ללקוח
          </button>
        </div>
      </div>

      {/* Summary and Tasks Panel */}
      <div className="space-y-4">
        {/* Summary Section */}
        <div className="bg-white rounded-lg border border-stone-200 p-4">
          <h3 className="text-lg font-medium text-finance-300 mb-2">סיכום שיחה</h3>
          <textarea 
            className="w-full h-32 p-2 border border-stone-200 rounded"
            value={summary}
            onChange={(e) => setSummary(e.target.value)}
          />
        </div>

        {/* Tasks Section */}
        <div className="bg-white rounded-lg border border-stone-200 p-4">
          <h3 className="text-lg font-medium text-finance-300 mb-2">משימות ליועץ</h3>
          <ul className="space-y-2">
            {tasks.map(task => (
              <li 
                key={task.id}
                className="flex items-center gap-2 p-2 bg-finance-50 rounded"
              >
                <input 
                  type="checkbox" 
                  checked={task.completed}
                  onChange={() => {/* Toggle task */}}
                />
                <span>{task.text}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default EnhancedTranscript;
