import React, { useState, useEffect } from 'react';
import RegulatorySidebar from './components/RegulatorySidebar';
import EnhancedTranscript from './components/EnhancedTranscript';
import RecordingControls from './components/RecordingControls';

const Dashboard = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioStream, setAudioStream] = useState(null);
  const [recognition, setRecognition] = useState(null);
  const [transcript, setTranscript] = useState([]);
  const [summary, setSummary] = useState('');
  const [tasks, setTasks] = useState([]);
  const [regulatoryAnswers, setRegulatoryAnswers] = useState({});

  useEffect(() => {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = 'he-IL';

      recognition.onresult = (event) => {
        const transcript = Array.from(event.results)
          .map(result => result[0].transcript)
          .join('');

        handleTranscriptChange({
          text: transcript,
          speaker: 'detecting...',
          timestamp: new Date().toLocaleTimeString()
        });
      };

      setRecognition(recognition);
    }
  }, []);

  const handleTranscriptChange = (newTranscript) => {
    setTranscript(prev => [...prev, newTranscript]);
  };

  const handleStart = () => {
    setIsRecording(true);
    recognition?.start();
  };

  const handleStop = () => {
    setIsRecording(false);
    recognition?.stop();
  };

  const handleUpload = (file) => {
    // TODO: Implement file upload
    console.log("Uploading:", file);
  };

  const handleSummary = () => {
    // TODO: Implement AI summary generation
    setSummary("סיכום יופק אוטומטית...");
  };

  const handleTaskToggle = (index) => {
    setTasks(prev => prev.map((task, i) => 
      i === index ? {...task, completed: !task.completed} : task
    ));
  };

  const handleSendEmail = (type) => {
    // TODO: Implement email sending
    console.log(`Sending ${type} email`);
  };

  return (
    <div className="grid grid-cols-12 gap-4 p-2 h-screen">
      {/* Regulatory Sidebar - 20% */}
      <div className="col-span-2 h-full">
        <RegulatorySidebar onAnswerUpdate={setRegulatoryAnswers} />
      </div>

      {/* Main Transcript Area - 55% */}
      <div className="col-span-7 h-full flex flex-col">
        <section className="flex-1 bg-white rounded-xl shadow-lg border border-stone-200 flex flex-col">
          <h3 className="text-xl font-semibold text-finance-300 p-4 border-b border-stone-200">
            תמלול שיחה בשידור חי
          </h3>

          <div className="flex-1 overflow-hidden">
            <EnhancedTranscript 
              isRecording={isRecording}
              transcript={transcript}
              onTranscriptUpdate={handleTranscriptChange}
              className="h-full"
            />
          </div>

          <div className="p-4 border-t border-stone-200">
            <RecordingControls 
              isRecording={isRecording}
              onStart={handleStart}
              onStop={handleStop}
              onUpload={handleUpload}
              onSummary={handleSummary}
            />
          </div>
        </section>
      </div>

      {/* Summary & Tasks Area - 25% */}
      <div className="col-span-3 h-full flex flex-col gap-4">
        <section className="flex-1 bg-white rounded-xl shadow-lg p-4 border border-stone-200">
          <h3 className="text-lg font-semibold text-finance-300 mb-4">סיכום שיחה</h3>
          <textarea
            className="w-full h-[calc(100%-8rem)] p-3 border border-stone-200 rounded-lg resize-none"
            value={summary}
            onChange={(e) => setSummary(e.target.value)}
            placeholder="סיכום אוטומטי יופיע כאן..."
          />
          <div className="mt-4 space-y-2">
            <button 
              onClick={() => handleSendEmail('client')}
              className="w-full py-2 px-4 bg-trust-400 text-white rounded-lg hover:bg-trust-500"
            >
              שלח מייל ללקוח
            </button>
            <button 
              onClick={() => handleSendEmail('advisor')}
              className="w-full py-2 px-4 bg-finance-300 text-white rounded-lg hover:bg-finance-400"
            >
              שלח מייל למשווק
            </button>
          </div>
        </section>

        <section className="h-1/3 bg-white rounded-xl shadow-lg p-4 border border-stone-200 overflow-auto">
          <h3 className="text-lg font-semibold text-finance-300 mb-4">משימות למעקב</h3>
          <div className="space-y-2">
            {tasks.map((task, index) => (
              <div key={index} className="flex items-center gap-2 p-2 bg-finance-50 rounded-lg">
                <input 
                  type="checkbox" 
                  checked={task.completed}
                  onChange={() => handleTaskToggle(index)}
                />
                <span className={task.completed ? 'line-through text-gray-500' : ''}>
                  {task.text}
                </span>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Dashboard;
