import React from 'react';
import RealTimeTranscription from './RealTimeTranscription';

const Dashboard = () => {
  // Callback that is called when transcription stops
  const handleTranscriptionComplete = (finalTranscript) => {
    console.log("תמלול הושלם:", finalTranscript);
    // Here you can later implement summarization or trigger email sending
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <header style={{ borderBottom: "1px solid #ddd", marginBottom: "20px" }}>
        <h1>Movne Platform – מערכת ייעוץ פיננסי</h1>
        <nav style={{ marginTop: "10px" }}>
          <a href="/crm" style={{ marginRight: "15px" }}>מערכת CRM</a>
          <a href="/client-ticket" style={{ marginRight: "15px" }}>תיק לקוח</a>
          <a href="/investment-file">תיק השקעות</a>
        </nav>
      </header>

      {/* Include the transcription component */}
      <RealTimeTranscription onTranscriptionComplete={handleTranscriptionComplete} />

      {/* 
        Here you can later add additional sections such as:
         - Regulatory Questions
         - Email sending buttons for advisor and client
         - A summary section for the conversation 
      */}
    </div>
  );
};

export default Dashboard;
