import React, { useState } from 'react';
import RealTimeTranscription from './RealTimeTranscription';

const Dashboard = () => {
  const [selectedLanguage, setSelectedLanguage] = useState("en-US");
  const [summary, setSummary] = useState("");
  const [transcriptData, setTranscriptData] = useState([]);

  const handleLanguageChange = (e) => {
    setSelectedLanguage(e.target.value);
  };

  const handleTranscriptionComplete = (transcript) => {
    setTranscriptData(transcript);
    // Simulate creating a conversation summary by concatenating the transcript text.
    const fullText = transcript.map(t => t.text).join(" ");
    const simulatedSummary = "סיכום השיחה: " + fullText.slice(0, 100) + "...";
    setSummary(simulatedSummary);
  };

  const handleSendEmails = () => {
    // Simulate sending emails to the advisor and the client.
    alert("מייל נשלח ליועץ וללקוח עם סיכום השיחה!");
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <header style={{ borderBottom: "1px solid #ddd", marginBottom: "20px" }}>
        <h1>Movne Platform – מערכת ייעוץ פיננסי</h1>
        <nav style={{ marginTop: "10px" }}>
          <a href="/crm" style={{ marginRight: "15px" }}>מערכת CRM</a>
          <a href="/client-ticket" style={{ marginRight: "15px" }}>תיק לקוח</a>
          <a href="/investment-file">תיק השקעות</a>
        </nav>
      </header>

      <div style={{ marginBottom: "20px" }}>
        <label style={{ marginRight: "10px" }}>בחר שפה:</label>
        <select value={selectedLanguage} onChange={handleLanguageChange}>
          <option value="en-US">English</option>
          <option value="he-IL">עברית</option>
        </select>
      </div>

      <RealTimeTranscription language={selectedLanguage} onTranscriptionComplete={handleTranscriptionComplete} />

      {summary && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid #aaa", borderRadius: "4px", backgroundColor: "#eef" }}>
          <h4>סיכום השיחה</h4>
          <p>{summary}</p>
        </div>
      )}

      <div style={{ marginTop: "20px" }}>
        <button
          onClick={handleSendEmails}
          style={{ padding: "10px 20px", backgroundColor: "#337ab7", color: "#fff", border: "none", borderRadius: "4px", cursor: "pointer" }}
        >
          שלח מייל
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
