import React from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import Dashboard from './Dashboard';
import CRM from './CRM';
import ClientTicket from './ClientTicket';
import InvestmentFile from './InvestmentFile';
import './App.css';

function App() {
  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <h1>Movne Platform – מערכת ייעוץ פיננסי</h1>
          <nav className="nav-tabs">
            <NavLink to="/" end className={({ isActive }) => isActive ? "active-tab" : "nav-link"}>
              Dashboard
            </NavLink>
            <NavLink to="/crm" className={({ isActive }) => isActive ? "active-tab" : "nav-link"}>
              CRM
            </NavLink>
            <NavLink to="/client-ticket" className={({ isActive }) => isActive ? "active-tab" : "nav-link"}>
              תיק לקוח
            </NavLink>
            <NavLink to="/investment-file" className={({ isActive }) => isActive ? "active-tab" : "nav-link"}>
              תיק השקעות
            </NavLink>
          </nav>
        </header>
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/crm" element={<CRM />} />
            <Route path="/client-ticket" element={<ClientTicket />} />
            <Route path="/investment-file" element={<InvestmentFile />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
