import React, { useState, useEffect } from 'react';
import { RecordingService } from '../services/recordingService';

const RecordingControls = ({ isRecording, onStart, onStop, onUpload }) => {
  const [duration, setDuration] = useState(0);
  const [recordingService] = useState(() => new RecordingService());
  const [recordings, setRecordings] = useState([]);

  useEffect(() => {
    const handleDurationUpdate = (event) => {
      setDuration(event.detail.duration);
    };

    window.addEventListener('recordingDurationUpdate', handleDurationUpdate);
    loadRecordings();

    return () => {
      window.removeEventListener('recordingDurationUpdate', handleDurationUpdate);
    };
  }, []);

  const loadRecordings = async () => {
    const savedRecordings = await recordingService.getRecordings();
    setRecordings(savedRecordings);
  };

  const handleStart = async () => {
    const started = await recordingService.startRecording();
    if (started) {
      onStart();
    }
  };

  const handleStop = async () => {
    const recordingData = await recordingService.stopRecording();
    onStop(recordingData);
    await loadRecordings();
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center gap-3">
        <button
          onClick={isRecording ? handleStop : handleStart}
          className={`px-4 py-2 rounded-lg transition-all duration-300 text-sm flex items-center gap-2
            ${isRecording 
              ? 'bg-red-500 hover:bg-red-600 text-white' 
              : 'bg-trust-400 hover:bg-trust-500 text-white'}`}
        >
          {isRecording ? (
            <>
              <span className="w-2 h-2 bg-white rounded-full animate-pulse"></span>
              הפסק הקלטה
              <span className="text-xs opacity-75">
                {recordingService.formatDuration(duration)}
              </span>
            </>
          ) : (
            'התחל הקלטה'
          )}
        </button>

        <label className="px-4 py-2 bg-finance-300 text-white rounded-lg hover:bg-finance-400 
                       transition-all duration-300 cursor-pointer text-sm">
          העלה הקלטה
          <input 
            type="file" 
            accept="audio/*" 
            onChange={(e) => onUpload(e.target.files[0])}
            className="hidden" 
          />
        </label>
      </div>

      {recordings.length > 0 && (
        <div className="mt-4">
          <h4 className="text-sm font-medium text-finance-300 mb-2">הקלטות קודמות</h4>
          <div className="space-y-2 max-h-40 overflow-y-auto">
            {recordings.map((recording) => (
              <div key={recording.id} 
                   className="flex items-center justify-between p-2 bg-gray-50 rounded-lg">
                <div className="text-sm">
                  <div>{new Date(recording.timestamp).toLocaleString()}</div>
                  <div className="text-xs text-gray-500">
                    {recordingService.formatDuration(recording.duration)}
                  </div>
                </div>
                <audio src={recording.url} controls className="h-8" />
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default RecordingControls;
